CREATE TABLE `pzpn`.`clubs` ( `id` INT(10) NOT NULL AUTO_INCREMENT , `chairman` VARCHAR(1024) NOT NULL , `code` VARCHAR(1024) NOT NULL , `email` VARCHAR(1024) NOT NULL , `facebook_url` VARCHAR(1024) NOT NULL , `foundation_year` VARCHAR(1024) NOT NULL , `instagram_url` VARCHAR(1024) NOT NULL , `main_team_coach` INT(10) NOT NULL , `background_photo` VARCHAR(1024) NOT NULL , `logo` VARCHAR(1024) NOT NULL , `address` VARCHAR(1024) NOT NULL , `venue_name` VARCHAR(1024) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;